import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  int _selectedIndex = 0;
  List<Map<String, dynamic>> tasks = [
    {'title': 'Finish report', 'description': 'Quarterly report', 'priority': 'High', 'dueDate': '2025-08-10', 'completed': false, 'priorityColor': Colors.red},
    {'title': 'Team meeting', 'description': 'Project milestones', 'priority': 'Medium', 'dueDate': '2025-08-12', 'completed': true, 'priorityColor': Colors.orange},
    {'title': 'Update docs', 'description': 'API docs', 'priority': 'Low', 'dueDate': '2025-08-15', 'completed': false, 'priorityColor': Colors.green}
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.blue[50],
      appBar: AppBar(
        title: Text('Tasks', style: TextStyle(fontWeight: FontWeight.bold)),
        backgroundColor: Colors.blueGrey[600],
        foregroundColor: Colors.white,
        elevation: 1,
        actions: [IconButton(icon: Icon(Icons.notifications), onPressed: () {})],
      ),
      body: ListView.builder(
        padding: EdgeInsets.all(8),
        itemCount: tasks.length,
        itemBuilder: (context, index) {
          final task = tasks[index];
          return Card(
            margin: EdgeInsets.only(bottom: 8),
            elevation: 1,
            shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(6)),
            child: Padding(
              padding: EdgeInsets.all(8),
              child: Row(
                children: [
                  Checkbox(
                    value: task['completed'],
                    onChanged: (value) { setState(() { task['completed'] = value!; }); },
                    activeColor: Colors.blueGrey[400],
                  ),
                  SizedBox(width: 8),
                  Expanded(
                    child: Column(
                      crossAxisAlignment: CrossAxisAlignment.start,
                      children: [
                        Text(
                          task['title'],
                          style: TextStyle(fontSize: 14, fontWeight: FontWeight.bold, decoration: task['completed'] ? TextDecoration.lineThrough : null),
                        ),
                        SizedBox(height: 2),
                        Text(
                          task['description'],
                          style: TextStyle(fontSize: 12, color: Colors.grey[600]),
                        ),
                        SizedBox(height: 4),
                        Row(
                          children: [
                            Container(
                              padding: EdgeInsets.symmetric(horizontal: 6, vertical: 2),
                              decoration: BoxDecoration(color: task['priorityColor'], borderRadius: BorderRadius.circular(10)),
                              child: Text(task['priority'], style: TextStyle(color: Colors.white, fontSize: 10)),
                            ),
                            SizedBox(width: 8),
                            Icon(Icons.calendar_today, size: 12, color: Colors.grey[500]),
                            SizedBox(width: 2),
                            Text(task['dueDate'], style: TextStyle(fontSize: 10, color: Colors.grey[600])),
                          ],
                        ),
                      ],
                    ),
                  ),
                  IconButton(icon: Icon(Icons.more_vert, size: 18), onPressed: () {}),
                ],
              ),
            ),
          );
        },
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {},
        backgroundColor: Colors.blueGrey[400],
        mini: true,
        child: Icon(Icons.add, color: Colors.white),
      ),
      bottomNavigationBar: BottomNavigationBar(
        selectedItemColor: Colors.blueGrey[400],
        currentIndex: _selectedIndex,
        onTap: (index) { setState(() { _selectedIndex = index; }); },
        items: [
          BottomNavigationBarItem(icon: Icon(Icons.task_alt, size: 20), label: 'Tasks'),
          BottomNavigationBarItem(icon: Icon(Icons.folder_outlined, size: 20), label: 'Projects'),
          BottomNavigationBarItem(icon: Icon(Icons.person_outline, size: 20), label: 'Profile'),
        ],
      ),
    );
  }
}
