import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFFFFF0F5),
      appBar: AppBar(
        backgroundColor: Color(0xFFFFF0F5),
        elevation: 0,
        title: Text(
          'Files Hub',
          style: TextStyle(
            fontFamily: 'Pacifico',
            color: Colors.coral,
            fontSize: 24,
          ),
        ),
        actions: [
          IconButton(
            icon: Icon(Icons.search, color: Colors.coral),
            onPressed: () {},
          ),
          IconButton(
            icon: Icon(Icons.share, color: Colors.coral),
            onPressed: () {},
          ),
        ],
      ),
      body: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Padding(
            padding: EdgeInsets.all(16),
            child: Wrap(
              spacing: 12,
              runSpacing: 12,
              children: [
                _buildQuickAccessItem('Documents', Icons.description, Colors.coral),
                _buildQuickAccessItem('Downloads', Icons.download, Color(0xFF98FF98)),
                _buildQuickAccessItem('Images', Icons.image, Color(0xFFE6E6FA)),
                _buildQuickAccessItem('Videos', Icons.video_library, Color(0xFFFFDAB9)),
              ],
            ),
          ),
          Padding(
            padding: EdgeInsets.all(16),
            child: Text(
              'Recent Files',
              style: TextStyle(
                fontSize: 18,
                fontWeight: FontWeight.w600,
                color: Colors.coral,
              ),
            ),
          ),
          Expanded(
            child: GridView.count(
              crossAxisCount: 2,
              padding: EdgeInsets.all(16),
              crossAxisSpacing: 16,
              mainAxisSpacing: 16,
              children: [
                _buildFileItem('Plan.pdf', '1.5 MB', 'Today', Icons.picture_as_pdf, Colors.coral),
                _buildFileItem('Trips', '100 items', 'Yesterday', Icons.folder, Color(0xFF98FF98)),
                _buildFileItem('Songs', '1.3 GB', '2 days ago', Icons.folder, Color(0xFFE6E6FA)),
                _buildFileItem('Docs', '25 items', '1 week ago', Icons.folder, Color(0xFFFFDAB9)),
              ],
            ),
          ),
        ],
      ),
      floatingActionButton: FloatingActionButton(
        backgroundColor: Colors.coral,
        child: Icon(Icons.add),
        onPressed: () {},
      ),
      floatingActionButtonLocation: FloatingActionButtonLocation.startFloat,
    );
  }

  Widget _buildQuickAccessItem(String title, IconData icon, Color color) {
    return Container(
      width: 80,
      child: Column(
        children: [
          CircleAvatar(
            radius: 30,
            backgroundColor: color.withOpacity(0.2),
            child: Icon(icon, color: color, size: 30),
          ),
          SizedBox(height: 8),
          Text(
            title,
            style: TextStyle(fontSize: 12, color: Colors.coral),
            textAlign: TextAlign.center,
          ),
        ],
      ),
    );
  }

  Widget _buildFileItem(String name, String size, String date, IconData icon, Color color) {
    return Container(
      decoration: BoxDecoration(
        color: Color(0xFFFFF0F5),
        borderRadius: BorderRadius.circular(16),
        boxShadow: [
          BoxShadow(
            color: Colors.grey.withOpacity(0.2),
            spreadRadius: 2,
            blurRadius: 6,
            offset: Offset(0, 3),
          ),
        ],
      ),
      child: Padding(
        padding: EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Icon(icon, color: color, size: 40),
            SizedBox(height: 12),
            Text(
              name,
              style: TextStyle(
                fontSize: 14,
                fontWeight: FontWeight.w600,
                color: Colors.coral,
              ),
              maxLines: 2,
              overflow: TextOverflow.ellipsis,
            ),
            Spacer(),
            Text(
              size,
              style: TextStyle(fontSize: 12, color: Colors.grey[600]),
            ),
            Text(
              date,
              style: TextStyle(fontSize: 10, color: Colors.grey[500]),
            ),
          ],
        ),
      ),
    );
  }
}
