import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Chats', style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold)),
        backgroundColor: Colors.blue[900],
        elevation: 2,
        actions: [
          IconButton(
            icon: Icon(Icons.search, color: Colors.white),
            onPressed: () {},
          ),
        ],
      ),
      backgroundColor: Color(0xFF0A0E21),
      body: ListView.separated(
        itemCount: 15,
        separatorBuilder: (context, index) => Divider(height: 1, color: Colors.blue[800]),
        itemBuilder: (context, index) {
          return ListTile(
            contentPadding: EdgeInsets.symmetric(horizontal: 14, vertical: 7),
            leading: Container(
              width: 50,
              height: 50,
              child: CustomPaint(
                painter: HexagonPainter(),
                child: Center(child: Text('U${index + 1}', style: TextStyle(color: Colors.blue[200]))),
              ),
            ),
            title: Text(
              'User ${index + 1}',
              style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16, color: Colors.white),
            ),
            subtitle: Text(
              'Hey there! How are you doing today?',
              style: TextStyle(color: Colors.blue[300], fontSize: 14),
              maxLines: 1,
              overflow: TextOverflow.ellipsis,
            ),
            trailing: Text(
              '12:${(index % 60).toString().padLeft(2, '0')}',
              style: TextStyle(color: Colors.blue[300], fontSize: 12),
            ),
            onTap: () {},
          );
        },
      ),
    );
  }
}

class HexagonPainter extends CustomPainter {
  @override
  void paint(Canvas canvas, Size size) {
    final paint = Paint()..color = Colors.blue[800]!;
    final path = Path();
    final center = Offset(size.width / 2, size.height / 2);
    final radius = size.width / 2;
    for (int i = 0; i < 6; i++) {
      final angle = (math.pi / 3) * i;
      final x = center.dx + radius * math.cos(angle);
      final y = center.dy + radius * math.sin(angle);
      if (i == 0) {
        path.moveTo(x, y);
      } else {
        path.lineTo(x, y);
      }
    }
    path.close();
    canvas.drawPath(path, paint);
  }

  @override
  bool shouldRepaint(covariant CustomPainter oldDelegate) => false;
}
