import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFFF5F5F5),
      appBar: AppBar(
        backgroundColor: Color(0xFFF5F5F5),
        elevation: 0,
        title: Text(
          'Data Science 101',
          style: TextStyle(
            color: Colors.black87,
            fontSize: 20,
            fontWeight: FontWeight.bold,
          ),
        ),
        actions: [
          IconButton(
            icon: Icon(Icons.settings, color: Colors.black54),
            onPressed: () {},
          ),
        ],
      ),
      body: Column(
        children: [
          Container(
            padding: EdgeInsets.symmetric(horizontal: 12, vertical: 8),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  'Progress',
                  style: TextStyle(
                    fontSize: 16,
                    fontWeight: FontWeight.w600,
                    color: Colors.black87,
                  ),
                ),
                SizedBox(height: 6),
                LinearProgressIndicator(
                  value: 0.7,
                  backgroundColor: Colors.grey[300],
                  valueColor: AlwaysStoppedAnimation<Color>(Color(0xFF26A69A)),
                  minHeight: 4,
                ),
              ],
            ),
          ),
          Expanded(
            child: ListView.builder(
              padding: EdgeInsets.symmetric(horizontal: 12, vertical: 8),
              itemCount: 5,
              itemBuilder: (context, index) {
                final modules = [
                  {'title': 'Introduction to Data Science', 'duration': '2h', 'progress': 1.0, 'completed': true},
                  {'title': 'Data Analysis with Python', 'duration': '3h', 'progress': 0.8, 'completed': false},
                  {'title': 'Machine Learning Basics', 'duration': '4h', 'progress': 0.5, 'completed': false},
                  {'title': 'Data Visualization', 'duration': '2.5h', 'progress': 0.2, 'completed': false},
                  {'title': 'Capstone Project', 'duration': '5h', 'progress': 0.0, 'completed': false},
                ];
                final module = modules[index];
                return Container(
                  margin: EdgeInsets.only(bottom: 8),
                  color: Colors.white,
                  child: ListTile(
                    leading: Stack(
                      alignment: Alignment.center,
                      children: [
                        SizedBox(
                          width: 30,
                          height: 30,
                          child: CircularProgressIndicator(
                            value: module['progress'] as double,
                            backgroundColor: Colors.grey[200],
                            valueColor: AlwaysStoppedAnimation<Color>(
                              module['completed'] as bool ? Colors.green : Color(0xFF26A69A),
                            ),
                            strokeWidth: 2,
                          ),
                        ),
                        Text(
                          '${index + 1}',
                          style: TextStyle(fontSize: 12, fontWeight: FontWeight.bold),
                        ),
                      ],
                    ),
                    title: Text(
                      module['title'] as String,
                      style: TextStyle(fontSize: 14, fontWeight: FontWeight.w600),
                    ),
                    subtitle: Text(
                      module['duration'] as String,
                      style: TextStyle(fontSize: 12, color: Colors.black45),
                    ),
                    trailing: module['completed'] as bool
                        ? Icon(Icons.check_circle, color: Colors.green, size: 20)
                        : null,
                  ),
                );
              },
            ),
          ),
        ],
      ),
    );
  }
}
