import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFF1A237E),
      appBar: AppBar(
        backgroundColor: Colors.transparent,
        elevation: 0,
        title: Text(
          'Product Categories',
          style: TextStyle(
            color: Colors.amber[300],
            fontSize: 22,
            fontWeight: FontWeight.bold,
          ),
        ),
        centerTitle: true,
      ),
      body: Padding(
        padding: EdgeInsets.all(16.0),
        child: Column(
          children: [
            Row(
              children: [
                Expanded(
                  child: _buildCategoryCard(
                    Icons.shopping_bag,
                    'Electronics',
                    [Colors.blue[600]!, Colors.purple[600]!],
                    140,
                  ),
                ),
                SizedBox(width: 12),
                Expanded(
                  child: _buildCategoryCard(
                    Icons.headphones,
                    'Audio',
                    [Colors.green[600]!, Colors.teal[600]!],
                    120,
                  ),
                ),
              ],
            ),
            SizedBox(height: 12),
            Row(
              children: [
                Expanded(
                  child: _buildCategoryCard(
                    Icons.laptop,
                    'Computers',
                    [Colors.orange[600]!, Colors.red[600]!],
                    120,
                  ),
                ),
                SizedBox(width: 12),
                Expanded(
                  child: _buildCategoryCard(
                    Icons.checkroom,
                    'Fashion',
                    [Colors.pink[600]!, Colors.purple[600]!],
                    140,
                  ),
                ),
              ],
            ),
            SizedBox(height: 12),
            Row(
              children: [
                Expanded(
                  child: _buildCategoryCard(
                    Icons.book,
                    'Books',
                    [Colors.yellow[700]!, Colors.orange[700]!],
                    130,
                  ),
                ),
                SizedBox(width: 12),
                Expanded(
                  child: _buildCategoryCard(
                    Icons.home,
                    'Home',
                    [Colors.teal[600]!, Colors.blue[600]!],
                    130,
                  ),
                ),
              ],
            ),
          ],
        ),
      ),
    );
  }

  Widget _buildCategoryCard(IconData icon, String title, List<Color> gradientColors, double height) {
    return Container(
      height: height,
      decoration: BoxDecoration(
        gradient: LinearGradient(
          begin: Alignment.topLeft,
          end: Alignment.bottomRight,
          colors: gradientColors,
        ),
        borderRadius: BorderRadius.circular(16),
        border: Border.all(
          color: Colors.amber[300]!.withOpacity(0.3),
          width: 1,
        ),
        boxShadow: [
          BoxShadow(
            color: gradientColors[0].withOpacity(0.3),
            blurRadius: 8,
            offset: Offset(0, 4),
          ),
        ],
      ),
      child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          Icon(
            icon,
            size: 40,
            color: Colors.white,
          ),
          SizedBox(height: 12),
          Text(
            title,
            style: TextStyle(
              fontSize: 15,
              fontWeight: FontWeight.bold,
              color: Colors.white,
            ),
          ),
        ],
      ),
    );
  }
}
