  Widget _buildCategoryCard(String title, IconData icon, Color color, double spent, double budget, Color statusColor) {
    double percentage = spent / budget;
    return Container(
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: Color(0xFF1A1A1A),
        borderRadius: BorderRadius.circular(12),
        border: Border.all(color: color.withOpacity(0.3)),
        boxShadow: [BoxShadow(color: color.withOpacity(0.1), spreadRadius: 1, blurRadius: 8)],
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Row(
            children: [
              Container(
                padding: EdgeInsets.all(8),
                decoration: BoxDecoration(
                  color: color.withOpacity(0.2),
                  borderRadius: BorderRadius.circular(8),
                ),
                child: Icon(icon, color: color, size: 20),
              ),
              Spacer(),
              CircularProgressIndicator(
                value: percentage > 1 ? 1 : percentage,
                backgroundColor: Colors.grey[800],
                valueColor: AlwaysStoppedAnimation<Color>(statusColor),
                strokeWidth: 3,
              ),
            ],
          ),
          SizedBox(height: 12),
          Text(title, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 14, color: Colors.white)),
          SizedBox(height: 4),
          Text('$${spent.toInt()}/$${budget.toInt()}', style: TextStyle(color: Colors.grey[400], fontSize: 12)),
          Text('$${(budget - spent).toInt()} left', style: TextStyle(color: statusColor, fontSize: 11, fontWeight: FontWeight.w500)),
        ],
      ),
    );
  }

  Widget _buildTransactionItem(String title, IconData icon, String amount, String date) {
    return Padding(
      padding: EdgeInsets.symmetric(horizontal: 16, vertical: 12),
      child: Row(
        children: [
          Container(
            padding: EdgeInsets.all(8),
            decoration: BoxDecoration(
              color: Color(0xFF2A2A2A),
              borderRadius: BorderRadius.circular(8),
            ),
            child: Icon(icon, color: Colors.grey[400], size: 20),
          ),
          SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(title, style: TextStyle(fontWeight: FontWeight.w500, color: Colors.white)),
                Text(date, style: TextStyle(color: Colors.grey[500], fontSize: 12)),
              ],
            ),
          ),
          Text(amount, style: TextStyle(
            fontWeight: FontWeight.w600,
            color: amount.startsWith('+') ? Color(0xFF51CF66) : Color(0xFFFF6B6B),
          )),
        ],
      ),
    );
  }
}
